    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        // TODO add your handling code here:
        double peso = sldPeso.getValue();
        double altura = Double.valueOf(txtAltura.getText());
        
        double imc = (peso / (altura * altura));
        
        if (imc < 18.5) {
            lblResposta.setText("Abaixo do Peso Ideal");
        }else if (imc < 25) {
            lblResposta.setText("Peso normal");
        }else if (imc < 30) {
            lblResposta.setText("Excesso de peso");
        }else if (imc < 35) {
            lblResposta.setText("Obesidade grau I");
        }else if (imc < 40) {
            lblResposta.setText("Obesidade gau II");
        }else{
            lblResposta.setText("Obesidade grau III");
        }
        
        lblIMC.setText(String.valueOf(imc));
        
    }//GEN-LAST:event_btnCalcularActionPerformed

    private void sldPesoStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_sldPesoStateChanged
        // TODO add your handling code here:
        String p = String.valueOf(sldPeso.getValue());
        lblRespPeso.setText(p);
    }//GEN-LAST:event_sldPesoStateChanged

